";
                    initial-value: 50;
                    inherits: true;
                }
                @property --Rainbow_G {
                    syntax: "";
                    initial-value: 200;
                    inherits: true;
                }
                @property --Rainbow_B {
                    syntax: "";
                    initial-value: 50;
                    inherits: true;
                }

                @keyframes RotateR {
                    0% {
                        --Rainbow_R: 50;
                    }
                    100% {
                        --Rainbow_R: 200;
                    }
                }

                @keyframes RotateG {
                    0% {
                        --Rainbow_G: 200;
                    }
                    100% {
                        --Rainbow_G: 50;
                    }
                }

                @keyframes RotateB {
                    0% {
                        --Rainbow_B: 50;
                    }
                    100% {
                        --Rainbow_B: 200;
                    }
                }

                :root {
                    --Rainbow_R: 50;
                    --Rainbow_G: 200;
                    --Rainbow_B: 50;

                    --Rainbow: rgb(var(--Rainbow_R), var(--Rainbow_G), var(--Rainbow_B));
                    --Rainbow_alt: rgb(var(--Rainbow_B), var(--Rainbow_R), var(--Rainbow_G));

                    animation: RotateR 10s linear infinite alternate, RotateG 5s linear infinite alternate, RotateB 7s linear infinite alternate;
                }

                h1,
                h2,
                h3,
                h4,
                h5,
                h6 {
                    color: var(--Rainbow_alt);
                }

                a:link,
                a:visited {
                    color: inherit;
                    text-decoration: underline;
                }
                a:hover {
                    color: var(--Rainbow);
                    text-decoration: none;
                }
